import React from 'react';
import {forum} from '../data/Threads'
import {BrowserRouter as Router, Route} from 'react-router-dom';
import Thread from '../components/Thread'
import ThreadPreview from '../components/ThreadPreview'

function Forum() {
    return(
        <div className="forum">
            {forum.map((thread, index) => (
                <div>
                        <Route exact path={"/forum/" + thread.id} component={() => 
                            <Thread {...props} 
                                posts={forum[thread.id].posts}
                                title={forum[thread.id].title} 
                                id={forum[thread.id].id}
                                endorsed={forum[thread.id].endorsed}/>
                            }/>
                    <ThreadPreview className='thread' id={thread.id}>
                        {thread.title}
                    </ThreadPreview>
                </div>
            ))}
        </div>
    );
};

export default Forum;
