import React from 'react';

const Thread = ({match, forums}) => {
        {console.log(forum)}

        const currentThread = forums[match.params.threadId];

        return(
           <div>
                    <h1>{currentThread.title}</h1>
                    <div className='forum'>
                    {currentThread.posts.map((posts, index) => (
                            <div className='post'>
                                {posts}
                            </div>
                    ))}
                    </div>
            </div>

        )
}


export default Thread;
