import React, { Component } from 'react';
import {BrowserRouter as Router, Route} from 'react-router-dom';
import Home from '../containers/Home';
import ForumContainer from './ForumContainer';
import QuizIndex from '../components/QuizIndex';
import NavBar from '../containers/NavBar';
import axios from "axios";
import Thread from '../components/Thread'
import {forum} from '../data/Threads'

class App extends Component {
    state={
        users: [],
        quizzes: []
    }
    
    componentDidMount(){
        this.getDataFromDb();
    }
    
    getQuizzesFromDb = () => {
        fetch("http://localhost:3001/api/getQuizzes")
          .then(data => data.json())
          .then(res => {
              this.setState({ quizzes: res.data })
          });
    };

    render(){
        return(
        <Router>
            <React.Fragment>
                <NavBar/>
                <Route exact path='/' component={Home} />
                <Route path='/forum' render={routerProps => <ForumContainer {...routerProps} forum={forum} />} />
             <Route exact path='/quizzes' component={QuizIndex} />
            </React.Fragment>    
        </Router>
        );
    }
};

export default App;
