import React, { Component } from 'react';
import {BrowserRouter as Router, Route} from 'react-router-dom';
import Home from '../containers/Home';
import Forum from '../containers/Forum';
import QuizContainer from '../containers/QuizContainer';
import NavBar from '../containers/NavBar';
import {forum} from '../data/Threads'
import Thread from '../components/Thread'

class App extends Component {
    state={
        userData: [],
        forumData: []
    }
    componentDidMount(){
        this.getUserDataFromDb();
        this.getForumDataFromDb();
    }
    
    getUserDataFromDb = () => {
        fetch("http://localhost:3001/api/getUsers")
          .then(data => data.json())
          .then(res => this.setState({ userData: res.data }));
    };

    getForumDataFromDb = () => {
        fetch("http://localhost:3001/api/getForums")
          .then(data => data.json())
          .then(res => this.setState({ forumData: res.data }));
    };

    render(){
        return(
        <Router>
            <React.Fragment>
                {console.log(this.state.forumData)}
                <NavBar/>
                <Route exact path='/' component={Home} />
                <Route exact path='/forum' render={(Forum)} />
                {(this.state.forumData).map((thread, index) => (
                    <Route exact path={"/forum/" + thread.id} component={(props) => 
                        <Thread {...props} 
                            posts={forum[thread.id].posts}
                            title={forum[thread.id].title} 
                            id={forum[thread.id].id}
                            endorsed={forum[thread.id].endorsed}/>
                        }/>
                ))}
                <Route exact path='/quizzes' component={QuizContainer} />
            </React.Fragment>    
        </Router>
        );
    }
};

export default App;
